// import count UI component 
import CountUI from '../../components/Count'
// import connect for connect UI component&redux
import {connect} from 'react-redux'

// return value pass to UI component as props {key:value} -- state
function a(state) {
    return {count:state}
}

// return value pass to UI component as props {key:value} -- method to operate state
function b() {
    return {add:(data) => {
        // notify redux to execute add
        dispatch({type:'increment',data})
    }}
}

// create and export a count container component
export default connect(a,b)(CountUI)
